import React, { Fragment, useEffect, useState } from "react";



//View request function

const ViewRequestsID = ({id}) => {

    const [requests, setRequests] = useState([]);

    const getRequestsID = async () => {
        try {
            const response = await fetch(`http://localhost:5000/api/requests/${id}`);
            const jsonData = await response.json();
            
            setRequests(jsonData);
        } catch (err) {
            console.error(err.message);
        }
    }

    useEffect(() => {
        getRequestsID();
    } , []);

//



    return (
        <Fragment>
            <div style={{ backgroundColor: '#BED3AB', padding: '40px', borderRadius: '30px', margin: '60px' }}>
            <table class="table table-hover table-responsive-md">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Product Team Name</th>
                        <th>Support Team Required</th>
                        <th>Start Date</th>
                        <th>End Date</th>
                        <th>Priority</th>
                        <th>Status</th>
                        <th>Description</th>
                        <th>Approve</th>
                        <th>Reject</th>

                    </tr>
                </thead>
                <tbody>
                    {requests.map(request => (
                        <tr key={request.id}>
                            <td>{request.id}</td>
                            <td>{request.product_team_name}</td>
                            <td>{request.support_team_required}</td>
                            <td>{request.start_date}</td>
                            <td>{request.end_date}</td>
                            <td>{request.priority}</td>
                            <td>{request.status}</td>
                            <td>{request.description}</td>
                            <td>
                               <button className="btn btn-primary">Approve</button> 
                            </td>
                            <td>
                            <button className="btn btn-primary">Reject</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
        </Fragment>
    )
};

export default ViewRequestsID;

//next steps
//create api request for get data based on id 
//assign to view so that it pulls data intop the form
