import React from "react";
import { useState } from "react";
import { Button, Form, Modal } from "react-bootstrap";

const UpdateRequest = ({ request }) => {
    const [productTeamName, setProductTeamName] = useState(request.product_team_name);
    const [supportTeamRequired, setSupportTeamRequired] = useState(request.support_team_required);
    const [priority, setPriority] = useState(request.priority);
    const [status, setStatus] = useState(request.status);
    const [description, setDescription] = useState(request.description);

    const updateRequest = async (id) => {
        try {
            const body = { productTeamName, supportTeamRequired, priority, status, description };
            const response = await fetch(`http://localhost:5000/api/requests/${id}`, {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(body)
            });

            window.location = "/RequestManagement";
        } catch (err) {
            console.error(err.message);
        }
    }

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
        <>
            <Button variant="primary" onClick={handleShow}>
                Update
            </Button>

            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Update Request</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                    <label>Product Team Name:</label>
                    <input  type="text" 
                            className="form-control" 
                            style={{ padding: "5px", marginBottom: "20px" }} 
                            value={productTeamName} 
                            onChange={e => setProductTeamName(e.target.value)} />



                    <label>Support Team Required:</label>
                    <Form.Control as="select" 
                    style={{ marginBottom: '10px' }}
                    value={support_team_required}
                    onChange={(e) => setSupport_team_required(e.target.value)}
                    >
                        <option value="">Select Team...</option>
                        <option value="System Build">System Build</option>
                        <option value="Infrastructure Services">Infrastructure Services</option>
                    </Form.Control>

                    <label>Priority:</label>
                    <input  type="text" 
                            className="form-control" 
                            style={{ padding: "5px", marginBottom: "20px" }} 
                            value={priority} 
                            onChange={e => setPriority(e.target.value)} />
                    <label>Status:</label>
                    <input type="text" className="form-control" style={{ padding: "5px", marginBottom: "20px" }} value={status} onChange={e => setStatus(e.target.value)} />
                    <label>Description:</label>
                    <input type="text" className="form-control" style={{ padding: "5px", marginBottom: "20px" }} value={description} onChange={e => setDescription(e.target.value)} />
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={() => updateRequest(request.request_id)}>
                        Save Changes
                    </Button>
                    
                </Modal.Footer>
            </Modal>
        </>
    );
}

export default UpdateRequest;
