import React from "react";
import { useState } from "react";
import { Button, Modal } from "react-bootstrap";

const UpdateRequest = ({ request }) => {
    const [productTeamName, setProductTeamName] = useState(request.product_team_name);
    const [supportTeamRequired, setSupportTeamRequired] = useState(request.support_team_required);
    const [priority, setPriority] = useState(request.priority);
    const [status, setStatus] = useState(request.status);
    const [description, setDescription] = useState(request.description);

    const updateRequest = async (id) => {
        try {
            const body = { productTeamName, supportTeamRequired, priority, status, description };
            const response = await fetch(`http://localhost:5000/api/requests/${id}`, {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(body)
            });

            window.location = "/RequestManagement";
        } catch (err) {
            console.error(err.message);
        }
    }

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
        <>
            <Button variant="primary" onClick={handleShow}>
                Update
            </Button>

            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Update Request</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <label>Product Team Name:</label>
                    <input type="text" className="form-control" style={{ padding: "5px" }} value={productTeamName} onChange={e => setProductTeamName(e.target.value)} />
                    <label>Support Team Required:</label>
                    <input type="text" className="form-control" style={{ padding: "5px" }} value={supportTeamRequired} onChange={e => setSupportTeamRequired(e.target.value)} />
                    <label>Priority:</label>
                    <input type="text" className="form-control" style={{ padding: "5px" }} value={priority} onChange={e => setPriority(e.target.value)} />
                    <label>Status:</label>
                    <input type="text" className="form-control" style={{ padding: "5px" }} value={status} onChange={e => setStatus(e.target.value)} />
                    <label>Description:</label>
                    <input type="text" className="form-control" style={{ padding: "5px" }} value={description} onChange={e => setDescription(e.target.value)} />
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={() => updateRequest(request.request_id)}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

export default UpdateRequest;
